import React, { useState } from 'react';
import { askQuestion } from '../services/api';

function QA() {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async () => {
    if (!question.trim()) {
      setError('Please enter a question');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const result = await askQuestion(question);
      setAnswer(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-900 mb-4">Ask Questions</h2>
      <div className="max-w-xl mx-auto">
        <div className="mt-1">
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            rows={4}
            className="shadow-sm focus:ring-primary-500 focus:border-primary-500 block w-full sm:text-sm border-gray-300 rounded-md"
            placeholder="Ask a question about your documents..."
          />
        </div>
        <div className="mt-3">
          <button
            onClick={handleSubmit}
            disabled={loading}
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50"
          >
            {loading ? 'Getting Answer...' : 'Submit Question'}
          </button>
        </div>

        {error && (
          <div className="mt-4 p-4 bg-red-50 rounded-md">
            <p className="text-sm text-red-700">{error}</p>
          </div>
        )}

        {answer && (
          <div className="mt-6 space-y-4">
            {answer.map((item, index) => (
              <div key={index} className="bg-white shadow rounded-lg p-4">
                <p className="text-sm font-medium text-gray-900">{item.doc_id}</p>
                <p className="mt-2 text-sm text-gray-600">{item.content}</p>
                {item.page && item.paragraph && (
                  <p className="mt-2 text-xs text-gray-500">
                    Page {item.page} â€¢ Paragraph {item.paragraph}
                  </p>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default QA;
