}

function Documents() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState(null);
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [deleting, setDeleting] = useState(false);
  const [loadingContent, setLoadingContent] = useState(false);

  const fetchDocuments = async () => {
    try {
      const response = await api.get('/documents');
      setDocuments(response.data);
    } catch (err) {
      setError('Failed to fetch documents');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDocuments();
  }, []);

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a file');
      return;
    }

    setUploading(true);
    setError(null);

    try {
      await uploadDocument(file);
      setFile(null);
      fetchDocuments(); // Refresh the document list
    } catch (err) {
      setError(err.message);
    } finally {
      setUploading(false);
    }
  };

  const handleDelete = async (documentId) => {
    if (!window.confirm('Are you sure you want to delete this document?')) {
      return;
    }

    setDeleting(true);
    try {
      await deleteDocument(documentId);
      fetchDocuments(); // Refresh the document list
    } catch (err) {
      setError(err.message);
    } finally {
      setDeleting(false);
    }
  };

  const handleViewDocument = async (doc) => {
    setSelectedDocument(doc);
    setLoadingContent(true);
    try {
      const documentDetails = await getDocumentDetails(doc.id);
      setSelectedDocument(documentDetails);
    } catch (err) {
      setError('Failed to load document content');
    } finally {
      setLoadingContent(false);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-900 mb-4">Documents</h2>

      {/* Upload Section */}
      <div className="max-w-xl mx-auto mb-8">
        <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
          <div className="space-y-1 text-center">
            <svg
              className="mx-auto h-12 w-12 text-gray-400"
              stroke="currentColor"
              fill="none"
              viewBox="0 0 48 48"
              aria-hidden="true"
            >
              <path
                d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                strokeWidth={2}
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </svg>
            <div className="flex text-sm text-gray-600">
              <label
                htmlFor="file-upload"
                className="relative cursor-pointer bg-white rounded-md font-medium text-primary-600 hover:text-primary-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-primary-500"
              >
                <span>Upload a file</span>
                <input
                  id="file-upload"
                  name="file-upload"
                  type="file"
                  className="sr-only"
                  onChange={(e) => setFile(e.target.files[0])}
                  accept=".pdf,.jpg,.jpeg,.png,.txt"
                />
              </label>
              <p className="pl-1">or drag and drop</p>
            </div>
            <p className="text-xs text-gray-500">PDF, JPG, PNG, or TXT up to 10MB</p>
          </div>
        </div>

        {file && (
          <div className="mt-4 flex items-center justify-between">
            <p className="text-sm text-gray-600">{file.name}</p>
            <button
              onClick={handleUpload}
              disabled={uploading}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
            >
              {uploading ? 'Uploading...' : 'Upload'}
            </button>
          </div>
        )}

        {error && (
          <div className="mt-4 p-4 bg-red-50 rounded-md">
            <p className="text-sm text-red-700">{error}</p>
          </div>
        )}
      </div>

      {/* Document List */}
      <div className="mt-8">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Uploaded Documents</h3>
        {loading ? (
          <p className="text-sm text-gray-600">Loading documents...</p>
        ) : documents.length === 0 ? (
          <p className="text-sm text-gray-600">No documents uploaded yet</p>
        ) : (
          <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
            {documents.map((doc) => (
              <div key={doc.id} className="bg-white shadow rounded-lg p-4">
                <div className="flex flex-col space-y-2">
                  <div className="flex items-start justify-between">
                    <div className="flex-1 min-w-0 pr-4">
                      <p className="text-sm font-medium text-gray-900 truncate" title={doc.filename}>
                        {doc.filename}
                      </p>
                    <p className="text-xs text-gray-500">
                      Type: {doc.file_type} • Uploaded: {new Date(doc.created_at).toLocaleDateString()}
                    </p>
                  </div>
                    <div className="flex-shrink-0 flex space-x-2">
                    <button
                        onClick={() => handleViewDocument(doc)}
                        className="text-primary-600 hover:text-primary-700 px-2 py-1 rounded hover:bg-gray-50"
                    >
                      View
                    </button>
                    <button
                      onClick={() => handleDelete(doc.id)}
                      disabled={deleting}
                        className="text-red-600 hover:text-red-700 px-2 py-1 rounded hover:bg-gray-50"
                    >
                      {deleting ? 'Deleting...' : 'Delete'}
                    </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Document View Modal */}
      {selectedDocument && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-start mb-4">
                <h3 className="text-lg font-medium text-gray-900 truncate max-w-2xl" title={selectedDocument.filename}>
                  {selectedDocument.filename}
                </h3>
                <button
                  onClick={() => setSelectedDocument(null)}
                  className="text-gray-400 hover:text-gray-500"
                >
                  <span className="sr-only">Close</span>
                  <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              <div className="space-y-4">
                {loadingContent ? (
                  <div className="text-center py-8">
                    <p className="text-gray-500">Loading document content...</p>
                  </div>
                ) : selectedDocument.pages ? (
                  selectedDocument.pages.map((page) => (
                  <div key={page.page_number} className="border-t pt-4">
                    <h4 className="text-sm font-medium text-gray-900 mb-2">
                      Page {page.page_number}
                    </h4>
                    <div className="space-y-2">
                      {page.paragraphs.map((paragraph) => (
                        <p key={paragraph.paragraph_number} className="text-sm text-gray-600">
                          {paragraph.content}
                        </p>
                      ))}
                    </div>
                  </div>
                  ))
                ) : (
                  <div className="text-center py-8">
                    <p className="text-gray-500">No content available</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

function Search() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [searching, setSearching] = useState(false);
  const [error, setError] = useState(null);
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [loadingContent, setLoadingContent] = useState(false);

  const handleSearch = async () => {
    if (!query.trim()) {
      setError('Please enter a search query');
      return;
    }

    setSearching(true);
    setError(null);

    try {
      const searchResults = await searchDocuments(query);
      setResults(searchResults);
    } catch (err) {
      setError(err.message);
    } finally {
      setSearching(false);
    }
  };

  const handleViewReference = async (docId, pageNumber, paragraphNumber) => {
    setLoadingContent(true);
    try {
      const documentDetails = await getDocumentDetails(docId);
      setSelectedDocument({
        ...documentDetails,
        highlightPage: pageNumber,
        highlightParagraph: paragraphNumber
      });
    } catch (err) {
      setError('Failed to load document content');
    } finally {
      setLoadingContent(false);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-900 mb-4">Search Documents</h2>
      <div className="max-w-xl mx-auto">
        <div className="mt-1 relative rounded-md shadow-sm">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            className="focus:ring-primary-500 focus:border-primary-500 block w-full pl-4 pr-12 sm:text-sm border-gray-300 rounded-md"
            placeholder="Search your documents..."
          />
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
            <button
              onClick={handleSearch}
              disabled={searching}
              className="text-primary-600 hover:text-primary-700 focus:outline-none"
            >
              <MagnifyingGlassIcon className="h-5 w-5" aria-hidden="true" />
            </button>
          </div>
        </div>

        {error && (
          <div className="mt-4 p-4 bg-red-50 rounded-md">
            <p className="text-sm text-red-700">{error}</p>
          </div>
        )}

        {searching && (
          <div className="mt-4 text-center">
            <p className="text-sm text-gray-600">Searching...</p>
          </div>
        )}

        {results.length > 0 && (
          <div className="mt-6 space-y-4">
            {results.map((result, index) => (
              <div key={index} className="bg-white shadow rounded-lg p-4">
                <p className="text-sm text-gray-900">{result.text}</p>
                <div className="mt-2 flex items-center justify-between">
                  <p className="text-xs text-gray-500">
                    Document ID: {result.metadata.doc_id} • Page: {result.metadata.page}
                  </p>
                  <button
                    onClick={() => handleViewReference(
                      result.metadata.doc_id,
                      result.metadata.page,
                      result.metadata.paragraph
                    )}
                    className="text-primary-600 hover:text-primary-700 text-sm"
                  >
                    View Reference →
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {!searching && results.length === 0 && query && (
          <div className="mt-4 text-center">
            <p className="text-sm text-gray-600">No results found</p>
          </div>
        )}
      </div>

      {/* Document View Modal */}
      {selectedDocument && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-start mb-4">
                <h3 className="text-lg font-medium text-gray-900 truncate max-w-2xl" title={selectedDocument.filename}>
                  {selectedDocument.filename}
                </h3>
                <button
                  onClick={() => setSelectedDocument(null)}
                  className="text-gray-400 hover:text-gray-500"
                >
                  <span className="sr-only">Close</span>
                  <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              <div className="space-y-4">
                {loadingContent ? (
                  <div className="text-center py-8">
                    <p className="text-gray-500">Loading document content...</p>
                  </div>
                ) : selectedDocument.pages ? (
                  selectedDocument.pages.map((page) => (
                    <div 
                      key={page.page_number} 
                      className={`border-t pt-4 ${
                        page.page_number === selectedDocument.highlightPage ? 'bg-yellow-50' : ''
                      }`}
                    >
                      <h4 className="text-sm font-medium text-gray-900 mb-2">
                        Page {page.page_number}
                      </h4>
                      <div className="space-y-2">
                        {page.paragraphs.map((paragraph) => (
                          <p 
                            key={paragraph.paragraph_number} 
                            className={`text-sm text-gray-600 p-2 rounded ${
                              page.page_number === selectedDocument.highlightPage && 
                              paragraph.paragraph_number === selectedDocument.highlightParagraph
                                ? 'bg-yellow-100 border border-yellow-200'
                                : ''
                            }`}
                          >
                            {paragraph.content}
                          </p>
                        ))}
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-8">
                    <p className="text-gray-500">No content available</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

function QA() {
