import React, { useState } from 'react';
import { MagnifyingGlassIcon } from '@heroicons/react/24/outline';
import { searchDocuments } from '../services/api';

function Search() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [searching, setSearching] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    if (!query.trim()) {
      setError('Please enter a search query');
      return;
    }

    setSearching(true);
    setError(null);

    try {
      const searchResults = await searchDocuments(query);
      setResults(searchResults);
    } catch (err) {
      setError(err.message);
    } finally {
      setSearching(false);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-900 mb-4">Search Documents</h2>
      <div className="max-w-xl mx-auto">
        <div className="mt-1 relative rounded-md shadow-sm">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            className="focus:ring-primary-500 focus:border-primary-500 block w-full pl-4 pr-12 sm:text-sm border-gray-300 rounded-md"
            placeholder="Search your documents..."
          />
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
            <button
              onClick={handleSearch}
              disabled={searching}
              className="text-primary-600 hover:text-primary-700 focus:outline-none"
            >
              <MagnifyingGlassIcon className="h-5 w-5" aria-hidden="true" />
            </button>
          </div>
        </div>

        {error && (
          <div className="mt-4 p-4 bg-red-50 rounded-md">
            <p className="text-sm text-red-700">{error}</p>
          </div>
        )}

        {searching && (
          <div className="mt-4 text-center">
            <p className="text-sm text-gray-600">Searching...</p>
          </div>
        )}

        {results.length > 0 && (
          <div className="mt-6 space-y-4">
            {results.map((result, index) => (
              <div key={index} className="bg-white shadow rounded-lg p-4">
                <p className="text-sm text-gray-900">{result.text}</p>
                <div className="mt-2 flex items-center justify-between">
                  <p className="text-xs text-gray-500">
                    Document ID: {result.metadata.doc_id} â€¢ Page: {result.metadata.page}
                  </p>
                  <p className="text-xs text-gray-500">
                    Similarity: {((1 - result.distance) * 100).toFixed(1)}%
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}

        {!searching && results.length === 0 && query && (
          <div className="mt-4 text-center">
            <p className="text-sm text-gray-600">No results found</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Search;
