import React from 'react';
import { Link } from 'react-router-dom';
import { DocumentIcon, MagnifyingGlassIcon, QuestionMarkCircleIcon } from '@heroicons/react/24/outline';

function Home() {
  const features = [
    {
      name: 'Document Management',
      description: 'Upload and manage your documents with ease. Support for PDF, images, and text files.',
      icon: DocumentIcon,
      link: '/documents',
    },
    {
      name: 'Semantic Search',
      description: 'Search through your documents using natural language queries.',
      icon: MagnifyingGlassIcon,
      link: '/search',
    },
    {
      name: 'Question & Answer',
      description: 'Ask questions about your documents and get AI-powered answers.',
      icon: QuestionMarkCircleIcon,
      link: '/qa',
    },
  ];

  return (
    <div className="text-center">
      <h2 className="text-3xl font-bold text-gray-900 mb-4">Welcome to Document Processing</h2>
      <p className="text-lg text-gray-600 mb-12">Upload, search, and analyze your documents with ease.</p>

      <div className="grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3">
        {features.map((feature) => (
          <Link
            key={feature.name}
            to={feature.link}
            className="relative group bg-white p-6 rounded-lg shadow hover:shadow-md transition-shadow"
          >
            <div>
              <feature.icon className="h-12 w-12 text-primary-600 mx-auto" aria-hidden="true" />
              <h3 className="mt-4 text-lg font-medium text-gray-900">{feature.name}</h3>
              <p className="mt-2 text-sm text-gray-500">{feature.description}</p>
            </div>
            <div className="mt-4">
              <span className="text-primary-600 group-hover:text-primary-700 text-sm font-medium">
                Get started â†’
              </span>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Home;
